import letzpwn from "../Assets/CaptureTheFlag/letzpwn.png";
import rr from "../Assets/CaptureTheFlag/rr.png";
import {Flag} from "phosphor-react";

const Team = function ({teamname, logo, homepage, ctftime}) {
    return (
        <div className="max-w-md py-3 transition duration-500 hover:scale-105 rounded-lg bg-blue-100">
            <div className="px-5 flex items-center justify-between">
                <div >
                    <a href={`https://${homepage}`}>
                        <h4 className="mb-2 text-2xl font-bold text-white">{teamname}</h4>
                    </a>
                    <p/>
                    <a href={`https://ctftime.org/team/${ctftime}`}>
                        <h5 className="mb-2 text-xl font-medium text-white">CtfTime</h5>
                    </a>
                </div>
                <img className="m-5 h-20" src={logo} alt=""/>
            </div>
        </div>
    );
};

const Ctf = function () {
    return (
        <div className="flex-col pt-9">
            <div className="text-center text-white text-4xl pb-3 font-medium">
                Capture The Flag Teams
            </div>
            <div
                className="p-6 grid grid-cols-1 sm:grid-cols-1 sm:p-6 md:grid-cols-2 md:p-6 lg:grid-cols-2 lg:p-8 xl:grid-cols-2 xl:p-10 gap-8 m-auto max-w-6xl">
                <Team
                    teamname={"RoyalRoppers"}
                    logo={rr}
                    homepage={"royalroppers.team"}
                    ctftime={"176008"}
                />
                <Team
                    teamname={"LetzPwn"}
                    logo={letzpwn}
                    homepage={"letzpwn.com"}
                    ctftime={"78388"}
                />

            </div>
        </div>
    );
};

export default Ctf;
